    <span class="token keyword">public</span> <span class="token keyword">void</span> <span class="token function">initialize</span><span class="token punctuation">(</span><span class="token class-name">MaxLength</span> constraintAnnotation<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        maxLength <span class="token operator">=</span> constraintAnnotation<span class="token punctuation">;</span>
