    <span class="token comment">// 返回类型</span>
    <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">Class</span><span class="token generics"><span class="token punctuation">&lt;</span><span class="token operator">?</span><span class="token punctuation">></span></span> returnType<span class="token punctuation">;</span>
    <span class="token comment">// 如果返回的是map类型，记录所有key</span>
    <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">String</span> mapKey<span class="token punctuation">;</span>
    <span class="token comment">// 返回resultHandler参数类型</span>
    <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">Integer</span> resultHandlerIndex<span class="token punctuation">;</span>
    <span class="token comment">// rowBounds参数位置</span>
    <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">Integer</span> rowBoundsIndex<span class="token punctuation">;</span>
    <span class="token comment">// 参数名解析起</span>
