  <span class="token comment">// 活跃的链接</span>
  <span class="token keyword">protected</span> <span class="token keyword">final</span> <span class="token class-name">List</span><span class="token generics"><span class="token punctuation">&lt;</span><span class="token class-name">PooledConnection</span><span class="token punctuation">></span></span> activeConnections <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">ArrayList</span><span class="token generics"><span class="token punctuation">&lt;</span><span class="token punctuation">></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token comment">// 链接请求次数</span>
  <span class="token keyword">protected</span> <span class="token keyword">long</span> requestCount <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span>
