<div class="language-java line-numbers-mode" data-ext="java"><pre v-pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">PooledDataSource</span> <span class="token keyword">implements</span> <span class="token class-name">DataSource</span> <span class="token punctuation">{</span>

  <span class="token keyword">private</span> <span class="token keyword">static</span> <span class="token keyword">final</span> <span class="token class-name">Log</span> log <span class="token operator">=</span> <span class="token class-name">LogFactory</span><span class="token punctuation">.</span><span class="token function">getLog</span><span class="token punctuation">(</span><span class="token class-name">PooledDataSource</span><span class="token punctuation">.</span><span class="token keyword">class</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token comment">// 链接池（存储了所有数据库链接和状态信息）</span>
  <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">PoolState</span> state <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">PoolState</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token comment">// 非链接池</span>
  <span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">UnpooledDataSource</span> dataSource<span class="token punctuation">;</span>

